import React, { Component } from 'react'

class Question extends Component {
  state = {
    question: "Hello World?",
    options: ["1", "2", "3"],
    correct: 2,
  }

  handleOption(option) {
    console.log(option);
  }

  render () {
    return (
      <div>
        <span>{this.state.question}</span> 
        <br />
        { this.state.options.map( option => <button onClick={ this.handleOption( option ) } key={option} id={ option }>{ option }</button> ) }
        <p>Riktig: {this.state.correct}</p>
      </div>
    )
  }
}

export default Question
