import React, { Component } from "react";
import Question from "./question";

export default class Questions extends Component {
  state = {
    score: 0,
    maxScore: 4,
    questions: [
      {
        id: 1,
        score: 1,
        question:
          "Hva kaller vi systemet som behandler alt som endres i et spill?",
        options: ["Event Handler", "InputControl", "Game Loop", "Renderer"],
        correct: 1,
        category: "",
      },
      {
        id: 2,
        score: 1,
        question: "Hva er hensikten med en variabel?",
        options: ["1", "2", "3", "4", "5", "6"],
        correct: 1,
        category: "",
      },
      {
        id: 3,
        score: 1,
        question: "Hva er forskjellen mellom HTML og CSS?",
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 4,
        score: 1,
        question: 'Hva er forskjellen mellom "==" og "===" i Javascript?',
        options: [],
        correct: 1,
        category: "",
      },
    ],
  };

  graderTilRadianer = (grader) => {
    return (Math.PI * (grader - 90)) / 180;
  };

  handleScore = (points) => {
    console.log("Event raised");
    this.setState({ score: this.state.score + points });
  };
  render() {
    var classes = "column is-one-third";
    return (
      <div className="columns">
        <div className={classes}></div>
        <div className={classes}>
          {this.state.questions.map((question) => (
            <Question
              score={question.score}
              onScore={this.handleScore}
              category={question.category}
              key={question.id}
              id={question.id}
              options={question.options}
              correct={question.correct}
            >
              {question.question}
            </Question>
          ))}
          <div>
            <h1 className="title is-3">
              Poeng: {this.state.score} av {this.state.maxScore}
            </h1>
            <canvas id="chart" width="300" height="300"></canvas>
          </div>
        </div>
      </div>
    );
  }
}
