import React, { Component } from 'react'

export default class Question extends Component {
  state = {
    question: this.props.question,
    options: this.props.options,
    correct: this.props.correct,
    id: this.props.id,
    category: this.props.category,
    score: this.props.score,
  }

  handleOption = chosen => {
    var options = this.state.options;
    console.log(chosen + this.state.id);
    if (chosen == options[this.state.correct - 1])
    {
      document.getElementById(chosen + this.state.id).className += " is-success";
      console.log("Riktig!");
    }
    else 
    {
      document.getElementById(chosen + this.state.id).className += " is-danger";
    }
  }

  render () {
    var questionClasses = "my-5 has-text-centered is-family-primary box is-radiusless"
    var btnClasses = "button mx-1 mt-2";
    var titleClasses= "title is-5";

    return (
      <div className={questionClasses}>
        <span className={titleClasses} >{this.props.question}</span> 
        <p>{this.props.category} ({this.props.score})</p>
        <br />
        { this.state.options.map( option => <button className={btnClasses} onClick={ () => this.handleOption(option) } key={option} id={ option + this.state.id }>{ option }</button> ) }
      </div>
    )
  }
}
