import React, { Component } from 'react'

export default class Question extends Component {
  state = {
    question: this.props.question,
    options: this.props.options,
    correct: this.props.correct,
  }

  handleOption = chosen => {
    var options = this.state.options;
    if (chosen == options[this.state.correct - 1])
    {
      console.log("Option ", options[chosen - 1], " was correct!");
    }
  }

  render () {
    return (
      <div>
        <span>{this.state.question}</span> 
        <br />
        { this.state.options.map( option => <button onClick={ () => this.handleOption(option) } key={option} id={ option }>{ option }</button> ) }
        <p>Riktig: {this.state.correct}</p>
      </div>
    )
  }
}
