import React, { Component } from 'react'

export default class Question extends Component {
  state = {
    question: this.props.question,
    options: this.props.options,
    correct: this.props.correct,
  }

  handleOption = chosen => {
    var options = this.state.options;
    if (chosen == options[this.state.correct - 1])
    {
      console.log("Option ", chosen - 1, " was correct!");
    }
  }

  render () {
    var questionClasses = "my-5 has-text-centered is-family-primary box is-radiusless"
    var btnClasses = "button is-info mx-1 mt-2";
    var titleClasses= "title is-5";

    return (
      <div className={questionClasses}>
        <span className={titleClasses} >{this.state.question}</span> 
        <br />
        { this.state.options.map( option => <button className={btnClasses} onClick={ () => this.handleOption(option) } key={option} id={ option }>{ option }</button> ) }
      </div>
    )
  }
}
