import React, { Component } from "react";
import Question from "./question";

export default class Questions extends Component {
  state = {
    score: 0,
    questions: [
      {
        id: 1,
        score: 2,
        question:
          "Hvem av disse forfatterene sto med nazistene under okkupasjonstiden?",
        options: ["Knut Hamsun", "Henrik Ibsen", "Jens Bjørneboe"],
        correct: 1,
        category: "Norsk",
      },

      {
        id: 2,
        score: 3,
        question: "Hvem utviklet kalkulus (integrasjon og derivasjon)?",
        options: ["Isaac Newton", "Albert Einstein", "Bernhard Riemann"],
        correct: 1,
        category: "Matte",
      },

      {
        id: 3,
        score: 1,
        question: "Fra et kronologisk perspektiv, hva kom først?",
        options: ["Den franske revolusjon", "Den amerikanske revolusjon"],
        correct: 2,
        category: "Historie",
      },
      {
        id: 4,
        score: 2,
        question:
          "Hvilket av disse javascript-kommandoene vil hente teksten i et inputfelt?",
        options: [
          "getValueFromId('inputfeltets ID')",
          "document.getElementById('inputfeltets ID').value",
        ],
        correct: 2,
        category: "IT2",
      },
      // {
      //   id: 5,
      //   score: 1,
      //   question: "Hva menes med en kvantitativ metode?",
      //   options: ["En fremgangsmåte basert på gjentatte forsøk", "En mattematisk metode", "Anvendelse av en kvantefysisk formel"],
      //   correct: 2,
      //   category: "TOF",
      // },
      {
        id: 6,
        score: 3,
        question: "Hva heter gymlæreren vår",
        options: ["Charlie", "Christer", "Katla"],
        correct: 1,
        category: "Gym",
      },
    ],
  };

  handleScore = (points) => {
    console.log("Event raised");
    this.setState({ score: this.state.score + points });
  };
  render() {
    var classes = "column is-one-third";
    return (
      <div className="columns">
        <div className={classes}></div>
        <div className={classes}>
          {this.state.questions.map((question) => (
            <Question
              score={question.score}
              onScore={this.handleScore}
              category={question.category}
              key={question.id}
              id={question.id}
              options={question.options}
              correct={question.correct}
            >
              {question.question}
            </Question>
          ))}
          <div>
            <h1 className="title is-3">Poeng: {this.state.score}</h1>
          </div>
        </div>
      </div>
    );
  }
}
