import React, { Component } from "react";
import Question from "./question";

export default class Questions extends Component {
  state = {
    score: 0,
    questions: [
      {
        id: 1,
        score: 2,
        question:
          "Hva er forskjellen mellom en variabel og en konstant i programmering?",
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 2,
        score: 2,
        question: "Hva er hensikten med en variabel?",
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 3,
        score: 2,
        question: "Hva er forskjellen mellom HTML og CSS?",
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 4,
        score: 2,
        question: 'Hva er forskjellen mellom "==" og "===" i Javascript?',
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 5,
        score: 2,
        question: "Hva vil følgende kode skrive ut i konsollen?",
        options: [],
        correct: 1,
        category: "",
      },
      {
        id: 6,
        score: 2,
        question: "",
        options: [],
        correct: 1,
        category: "",
      },
    ],
  };

  handleScore = (points) => {
    console.log("Event raised");
    this.setState({ score: this.state.score + points });
  };
  render() {
    var classes = "column is-one-third";
    return (
      <div className="columns">
        <div className={classes}></div>
        <div className={classes}>
          {this.state.questions.map((question) => (
            <Question
              score={question.score}
              onScore={this.handleScore}
              category={question.category}
              key={question.id}
              id={question.id}
              options={question.options}
              correct={question.correct}
            >
              {question.question}
            </Question>
          ))}
          <div>
            <h1 className="title is-3">Poeng: {this.state.score}</h1>
          </div>
        </div>
      </div>
    );
  }
}
